mapper.select:
SELECT

FROM sys_role a
WHERE a.name = #{name} AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT

FROM sys_role a
WHERE a.enname = #{enname} AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT , rd.datarule_id AS "dataRuleList.id", sr.nums as "nums" FROM sys_role a LEFT JOIN sys_user_role ur ON ur.role_id = a.id LEFT JOIN sys_user u ON u.id = ur.user_id LEFT JOIN sys_role_datarule rd ON rd.role_id = a.id LEFT JOIN (SELECT COUNT(*) as "nums",sur.role_id from sys_user su LEFT JOIN sys_user_role sur on sur.user_id = su.id GROUP BY sur.role_id) sr on sr.role_id = a.id WHERE a.del_flag = #{DEL_FLAG_NORMAL} AND a.useable=#{useable}
AND u.id = #{user.id}
AND u.login_name = #{user.loginName}
AND a.name =#{name}
AND a.enname like '%'||#{enname}||'%' CONCAT('%', #{enname}, '%') '%'+#{enname}+'%'
${dataScope} ORDER BY a.create_date

SELECT , sr.nums as "nums" FROM sys_role a LEFT JOIN sys_role_datarule rd ON rd.role_id = a.id LEFT JOIN (SELECT COUNT(1) as "nums",sur.role_id from sys_user su LEFT JOIN sys_user_role sur on sur.user_id = su.id where su.del_flag=0 and su.fatherid=0 GROUP BY sur.role_id) sr on sr.role_id = a.id WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND a.create_by =#{createById}
AND a.type =#{type}
ORDER BY a.create_date

SELECT , sr.nums as "nums" FROM sys_role a LEFT JOIN (SELECT COUNT(*) as "nums",sur.role_id from sys_user su LEFT JOIN sys_user_role sur on sur.user_id = su.id GROUP BY sur.role_id) sr on sr.role_id = a.id WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name =#{name}
ORDER BY a.create_date

mapper.update:
UPDATE sys_role SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
